import React, { PureComponent } from "react";
import styles from "./NavDrawer.module.css";
import LatestIcon from "../icons/LatestIcon";
import VideosIcon from "../icons/VideosIcon";
import ArticlesIcon from "../icons/ArticlesIcon";

class NavDrawer extends PureComponent {
  render() {
    return (
      <div className={styles.container}>
        <div
          className={
            props.filter === "latest"
              ? `${styles.filterOption} ${styles.selected}`
              : styles.filterOption
          }
          data-name={`latest`}
          onClick={props.filterFunction}
        >
          <LatestIcon filterFunction={props.filterFunction} />{" "}
          {props.width > 600 ? `Latest` : null}
        </div>
        <div
          className={
            props.filter === "videos"
              ? `${styles.filterOption} ${styles.selected}`
              : styles.filterOption
          }
          data-name={`videos`}
          onClick={props.filterFunction}
        >
          <VideosIcon filterFunction={props.filterFunction} />{" "}
          {props.width > 600 ? `Videos` : null}
        </div>
        <div
          className={
            props.filter === "articles"
              ? `${styles.filterOption} ${styles.selected}`
              : styles.filterOption
          }
          data-name={`articles`}
          onClick={props.filterFunction}
        >
          <ArticlesIcon filterFunction={props.filterFunction} />{" "}
          {props.width > 600 ? `Articles` : null}
        </div>
      </div>
    );
  }
}

export default NavDrawer;
